import React, { useState, useEffect } from 'react';

import MessageDialog from '../../dialog/MessageDialog/MessageDialog';
import Modal from '../../aux/Modal.js';

import './styles.scss';

export default function MessageDisplay() {
    const [opened, setOpened] = useState(false);
    const [dialog, setDialog] = useState(false);

    return(
        <>
            <div onClick={() => {
                setDialog(true);
                setOpened(true);
                }} className="msg-element">
                <ul>
                    <li><i className="material-icons">
                    {`${opened === true ? 'drafts' : 'mail'}`}</i>
                    </li>
                    <li>
                    <p className="msg-preview">Lorem ipsum dolor gut of something....</p>
                    </li>
                    <li>
                        <p className="msg-sender">Mensagem do sistema</p>
                    </li>
                    <li>
                        <p className="msg-date">24/12/1994</p>
                    </li>
                </ul>
            </div>
            {
                dialog &&
                    <Modal>
                        <MessageDialog handler={() => setDialog(false)} />
                    </Modal>
            }
        </>
    );
}
