import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addCart } from 'store/ducks/shop';
import Slider from 'react-slick';

import Dialog from '../../Dialog';

import './styles.scss';

export default function ArrangementDialog({ handler, el }) {
    //carousel
    function SampleNextArrow(props) {
        const { className, style, onClick } = props;
        return (
            <div
                className={"custom-arrow dialog-next"}
                style={{ ...style, display: "block" }}
                onClick={onClick}
            >
                <i class="material-icons">arrow_forward_ios</i>
            </div>
        );
    }

    function SamplePrevArrow(props) {
        const { className, style, onClick } = props;
        return (
            <div
                className={"custom-arrow dialog-prev"}
                style={{ ...style, display: "block" }}
                onClick={onClick}
            >
                <i class="material-icons">arrow_back_ios</i>
            </div>
        );
    }

    const settings = {
        arrows: true,
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />
    };

    const [like, setLike] = useState(false);

    const likeHandle = () => {
        setLike(true);
    }

    const cart = useSelector(state => state.shop.cart)

    const dispatch = useDispatch();
    const addToCart = () => {
        dispatch(addCart({
            'id' : el.project_id,
            'name': el.name,
            'imagem': el.pictures.file1,
        }))
    }

    return(
        <Dialog handler={handler}>
            <div className="arrangement-landscape">
                <div className="arrangement-landscape__picture">
                    <Slider {...settings}>
                        <div className="arrangement-landscape__picture--slide">
                            <img className="slide-img" src={el.pictures.file1} alt='Foto'/>
                        </div>
                        <div className="arrangement-landscape__picture--slide">
                            <img className="slide-img" src={el.pictures.file2} alt='Foto'/>
                        </div>
                        <div className="arrangement-landscape__picture--slide">
                            <img className="slide-img" src={el.pictures.file3} alt='Foto'/>
                        </div>
                    </Slider>
                </div>
                <div className="arrangement-landscape__about">
                    <div className="arrangement-landscape__about--header">
                        <h1 className="arrangement-title">{el.name}</h1>
                        <button onClick={addToCart} 
                            className="app-btn">Solicitar</button>
                    </div>
                    <div className="arrangement-landscape__about--desc">
                        <div className="arrangement-autor">
                            <img className="arrangement-autor__img" src={el.autor_pic} />
                            <p className="arrangement-autor__name">{el.autor} <b>-</b></p>
                        </div>
                        <p className="arrangement-landscape__about--desc--text">
                            {el.description}
                        </p>
                    </div>
                    <div className="arrangement-landscape__about--footer">
                        <div className="arrangement-likes">
                            <p className="arrangement-likes__num">{el.likes}</p>
                            <button className="arrangement-likes__like">
                                <i className={`material-icons 
                                ${like && 'liked'}`}>
                                    { like === true ? 'favorite' : 'favorite_border' }
                                </i>
                            </button>
                        </div>
                        <button className="app-btn btn-filled arr-btn">Aulas</button>
                    </div>
                </div>
            </div>
            <div className="arrangement-portrait">
                <div className="arrangement-portrait__header">
                    <h1 className="arrangement-title">{el.name}</h1>
                    <button onClick={addToCart} 
                        className="app-btn order-btn">Solicitar</button>
                </div>
                <div className="arrangement-portrait__picture">
                    <Slider {...settings}>
                        <div className="arrangement-portrait__picture--slide">
                            <img className="slide-img" src={el.picture.file1} />
                        </div>
                        <div className="arrangement-portrait__picture--slide">
                            <img className="slide-img" src={el.picture.file1} />
                        </div>
                        <div className="arrangement-portrait__picture--slide">
                            <img className="slide-img" src={el.picture.file1} />
                        </div>
                    </Slider>
                </div>
                <div className="arrangement-portrait__desc">
                    <div className="arrangement-autor">
                        <img className="arrangement-autor__img" 
                            src="https://ikebana-app-content.s3-sa-east-1.amazonaws.com/static/mainlogo_transparent_notext.png" alt="perfil" />
                        <p className="arrangement-autor__name">Fabricio Dematte <b>-</b></p>
                    </div>
                    <p className="arrangement-portrait__desc--text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc sodales sodales eros, a auctor orci efficitur eu. Donec a euismod quam. Cras sit amet odio risus. Mauris sit amet quam nec massa consectetur gravida finibus vitae ex. Sed mattis metus quis fringilla porta. Phasellus imperdiet lectus eget sem dignissim elementum. Donec imperdiet ut dui eget consequat. Proin id dignissim eros.</p>
                </div>
                <div className="arrangement-portrait__footer">
                    <div className="arrangement-portrait__footer--likes">
                        <p className="arrangement-likes__num">1337</p>
                        <button className="arrangement-likes__like">
                            <i className={`material-icons 
                                ${like && 'liked'}`}>
                                { like === true ? 'favorite' : 
                                        'favorite_border' }
                            </i>
                        </button>
                    </div>
                    <button className="app-btn btn-filled lesson-btn">Aulas</button>
                </div>
            </div>
        </Dialog>
    );
}
