import React from 'react';
import { useHistory, useParams } from 'react-router-dom';
import api from 'services/api';

import './styles.scss';

export default function SingleLesson() {
    const [loading, setLoading] = useState(true);

    const fetchProject = async () => {
        try {
            const response = await api({
                method: 'get',
                url: `/get_project/${params.lesson_hash}`
            })
        } catch(e) {
            console.log(e)
        } finally {
            setLoading(false)
        }

    }

    const params = useParams();

    console.log(params.lesson_hash);

    const history = useHistory();

    return(
        <div className="single-lesson">
            <div className="single-lesson__content">
                <div className="single-lesson__content--header">
                    <h1 className="lesson-title">Nome da aula</h1>
                </div>
                <div className="single-lesson__content--video"></div>
                <div className="single-lesson__content--desc">
                    <p className="lesson-text">Etiam vestibulum sit amet sapien sit amet sagittis. Integer eget lacinia eros. Etiam tempus nunc nec nulla condimentum, et faucibus libero placerat. Praesent ligula tellus, mattis vitae feugiat sit amet, aliquam at enim. Praesent eu purus dolor. Praesent nisi magna, volutpat sit amet volutpat eu, porttitor eget tortor. Aliquam placerat, ligula sit amet hendrerit ullamcorper, lectus dui interdum tortor, id venenatis dui nisl finibus tellus. Duis velit tellus, fermentum et enim non, placerat eleifend enim. Phasellus ac nisl lacinia, mattis urna sit amet, tempus tellus. Quisque efficitur lectus ligula, et laoreet nunc posuere ut. Maecenas faucibus dolor quis fermentum euismod. Nullam tempus magna eget est mollis, non dictum erat finibus. Nullam hendrerit ante ex, eget sagittis neque mollis ac.Integer rutrum mauris dui, eget ultricies risus eleifend sed. Sed ornare, nibh id laoreet faucibus, libero justo vulputate mi, interdum suscipit libero lacus sed nulla. Donec tincidunt suscipit enim eget bibendum. Nulla nec odio et sem tincidunt efficitur at nec ante. Aenean est ante, porta eget leo a, ornare vehicula nisi. Sed dictum bibendum eros id sagittis. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus vel ante eget ex tempus convallis vel sit amet neque. Fusce ultrices ornare massa, nec iaculis risus porta ut. Ut vestibulum arcu sit amet sem volutpat, ut dictum leo posuere. Vivamus vitae libero elit.
                         viverra lectus, a dictum sem. Etiam et libero id purus dignissim fermentum. Vivamus sit amet egestas mauris. Donec ut metus nibh. Aliquam a lacus eget magna tempus efficitur. Curabitur vitae tempor libero, vitae varius magna. Aenean sed convallis ligula. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aliquam vel vestibulum eros.</p>
                </div>
                <div className="single-lesson__content--pic">
                    <div className="pic-container"></div>
                    <div className="pic-container"></div>
                    <div className="pic-container"></div>
                </div>
                <div className="single-lesson__content--footer">
                    <button onClick={() => history.goBack()} className="btn-back">Voltar</button>
                    <div className="footer-autor">
                        <img className="autor-img" 
                        src={"https://ikebana-app-users.s3-sa-east-1"+
                        ".amazonaws.com/perfil.png"} alt="Profile pic"/>
                        <p className="autor-name">fabricio.dematte</p>
                    </div>
                </div>
            </div>
        </div>
    );
}
