import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import './styles.scss';

import Modal from '../../aux/Modal';

export default function Motivation() {
    const history = useHistory();
    let delta = 0
    let op_delta = 0

    useEffect(() => {
        const current = document.getElementById("motivation-frame");
        const hero = document.getElementById("parallax_hero");

        current.onscroll = function() {parallax()};

        const parallax = () => {
            delta =+ current.scrollTop/3
            op_delta =+ current.scrollTop/431
            hero.style.backgroundPosition=`50% ${-delta}px`
            hero.style.opacity=`${1-op_delta}`;
        }
    }, [])


    useEffect(() => {
        document.getElementsByTagName("body")[0].style.overflow="hidden";

        return () => {
            document.getElementsByTagName("body")[0].style.overflow="auto";
        }
    },[])

    return(
        <Modal>
            <div id="motivation-frame" class="motivation">
                <div id="parallax_hero" className="motivation__hero">
                </div>
                <div className="motivation__header">
                    <h1 className="motivation__header--text">
                        Motivação
                    </h1>
                </div>
            </div>
        </Modal>
    );
}
