const initialState = {
    notifications: [],
}

export const pushNotification = (payload) => {
    return {
        type: "PUSH_NOTIFICATION",
        payload
    }
}

export default (state = initialState, action) => {
    switch(action.type) {
        case 'PUSH_NOTIFICATION': {
            const payload = action.payload
            return {
                notifications: [
                    ...state.notifications,
                    payload
                ]
            }
        }
        default: {
            return state;
        }
    }
};
