import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import ArrangementDialog from '../../Dialog/components/ArrangementDialog/ArrangementDialog';
import Modal from '../../aux/Modal.js';

import './styles.scss';



export default function ArrangementDisplay() {
    const [dialog, setDialog] = useState('');

    const options = {
        'visualize': ArrangementDialog,
    }

    const ActiveDialog = options[dialog];

    return(
        <>
            <div className={`arr-element ${dialog !== '' ? 'clicked' : ''}`}>
                <img className="img-cover" src="https://ikebana-app-content.s3-sa-east-1.amazonaws.com/Ikebana_-_Yoshiko_Nakamura_01B.jpg" alt="cover" />
                <div className="arr-element__content">
                    <button onClick={() => setDialog('visualize')} 
                        className="arr-open-btn">
                        <i className="material-icons">remove_red_eye</i>
                        Ver
                    </button>
                    <button
                        className="arr-edit-btn">
                        <i className="material-icons">build</i>
                        <Link to="/account/new_project">
                            Editar
                        </Link>
                    </button>
                    <ul className="arr-element__content--list">
                        <li className="arr-element__content--list list-el">
                            <h4 className="arr-title">Nome do arranjo</h4>
                        </li>
                        <div className="description-wrapper">
                            <li className="arr-element__content--list list-el">
                                <p className="arr-desc">1337
                                    <i className="material-icons">
                                        favorite</i>
                                </p>
                            </li>
                            <li className="arr-element__content--list list-el">
                                <p className="arr-desc">28/12/1994</p>
                            </li>
                        </div>
                    </ul>
                </div>
            </div>
            {
                dialog &&
                    <Modal>
                        <ActiveDialog handler={() => setDialog('')}/>
                    </Modal>
            }
        </>
    );
}
