import React, { useEffect, useState } from 'react';
import api from 'services/api';
import { useSelector } from 'react-redux';

import './styles.scss';

import LessonCard from '../../cards/LessonCard/LessonCard';

export default function Lessons() {
    const [data, setData] = useState(false);
    const [loading, setLoading] = useState(false);
    const authKey = useSelector(state => state.auth.keys)

    const fetchLessons = async (key=authKey) => {
        try {
            const response = await api({
                method: 'get',
                url: '/list',
                headers: {
                    'Authorization': `Bearer ${key[0].key}`
                },
            })
            response.data && setData(response.data.filter((el) => {
                return el.type === 'lesson'
            }))
        } catch(e) {
            console.log(e)
        } finally {
            setLoading(false)
        }
    }

    return(
        <div className="lessons-main">
            <div className="lessons-main__center">
                <i className="material-icons">search</i>
                <input id="lesson_search" type="text" placeholder="Pesquisar aulas..."/>
                <div className="lessons-main__center--filter">
                    <select id="lesson-filter" name="lessons">
                        <option value="arranjo">ARRANJOS</option>
                        <option value="concept">CONCEITOS</option>
                    </select>
                </div>
            </div>
            <div className="lessons-main__list">
                {
                    data.map((el) => <LessonCard {...el}/>)
                }
            </div>
        </div>
    );
}
