import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addCart } from 'store/ducks/shop';
import Slider from 'react-slick';
import api from 'services/api';

import Dialog from '../../Dialog';

import './styles.scss';

export default function ArrangementDialog({ handler, projectID }) {
    const authKey = useSelector(state => state.auth.keys)
    const [loading, setLoading] = useState(true);
    const [userID, setUserID] = useState(false);
    const [liked, setLiked] = useState(false)
    const [likeCount, setLikeCount] = useState(el.likes);
    const [data, setData] = useState(false);


    //carousel custom settings
    function SampleNextArrow(props) {
        const { className, style, onClick } = props;
        return (
            <div
                className={"custom-arrow dialog-next"}
                style={{ ...style, display: "block" }}
                onClick={onClick}
            >
                <i class="material-icons">arrow_forward_ios</i>
            </div>
        );
    }
    function SamplePrevArrow(props) {
        const { className, style, onClick } = props;
        return (
            <div
                className={"custom-arrow dialog-prev"}
                style={{ ...style, display: "block" }}
                onClick={onClick}
            >
                <i class="material-icons">arrow_back_ios</i>
            </div>
        );
    }
    const settings = {
        arrows: true,
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />
    };
    //end carousel settings


    //dispatching to cart
    const cart = useSelector(state => state.shop.cart)
    const dispatch = useDispatch();
    const addToCart = () => {
        dispatch(addCart({
            'id' : data.project_id,
            'name': data.name,
            'imagem': data.pictures.file1,
        }))
    }


    //fetching user_id to check like status
    const fetchUsername = async (key=authKey) => {
        try {
            const username = await api({
                method: 'get',
                url: '/user',
                headers: {
                    'Authorization': `Bearer ${key[0].key}`
                },
            })
            setUserID(username.data.id)
        } catch(e) {
            console.log(e)
        } finally {
            setLoading(false)
        }
    }


    //commiting like to db
    const commitLike = async (key=authKey) => {
        try {
            const response = await api({
                method: 'post',
                url: '/like_project',
                headers: {
                    'Authorization': `Bearer ${key[0].key}`
                },
                data: {
                    project_id: data.project_id
                }
            })
            console.log(response.data)
        } catch(e) {
            console.log(e)
        } 
    }

    const fetchProject = async () => {
        try {
            const response = await api({
                method: 'get',
                url: `/get_project/${projectID}`
            })
            setData(response.data)
        } catch(e) {
            console.log(e)
        } finally {
            setLoading(false)
        }
    }

    useEffect(() => {
        fetchProject()
        fetchUsername()
    },[])


    return(
        <Dialog handler={handler}>
            <div className="arrangement-landscape">
                <div className="arrangement-landscape__picture">
                    <Slider {...settings}>
                        {
                            data.pictures.file1 && 
                                <div className="arrangement-landscape__picture--slide">
                                    <img className="slide-img" src={data.pictures.file1} />
                                </div>
                        }
                        {
                            data.pictures.file2 && 
                                <div className="arrangement-landscape__picture--slide">
                                    <img className="slide-img" src={data.pictures.file2} />
                                </div>
                        }
                        {
                            data.pictures.file3 && 
                                <div className="arrangement-landscape__picture--slide">
                                    <img className="slide-img" src={data.pictures.file3} />
                                </div>
                        }
                    </Slider>
                </div>
                <div className="arrangement-landscape__about">
                    <div className="arrangement-landscape__about--header">
                        <h1 className="arrangement-title">{el.name}</h1>
                        <button onClick={addToCart} 
                            className="app-btn">Solicitar</button>
                    </div>
                    <div className="arrangement-landscape__about--desc">
                        <div className="arrangement-autor">
                            <img className="arrangement-autor__img" src={data.autor_pic} />
                            <p className="arrangement-autor__name">{data.autor_fullname} <b>-</b></p>
                        </div>
                        <p className="arrangement-landscape__about--desc--text">
                            {data.description}
                        </p>
                    </div>
                    <div className="arrangement-landscape__about--footer">
                        <div className="arrangement-likes">
                            <p className="arrangement-likes__num">{likeCount}</p>
                            <button onClick={() => {
                                    setLiked(true)
                                    commitLike()
                                    setLikeCount(likeCount + 1)
                                    console.log(likeCount)
                                }} className="arrangement-likes__like">
                                {
                                    Object.keys(data.liked_by).includes(userID.toString()) ?
                                        <i title="Curtido" 
                                    className="material-icons">favorite</i>
                                            :
                                        <i className="material-icons">{liked ? 'favorite' 
                                        : 'favorite_border'}</i>
                                }
                            </button>
                        </div>
                        <button className="app-btn btn-filled arr-btn">Aulas</button>
                    </div>
                </div>
            </div>
            <div className="arrangement-portrait">
                <div className="arrangement-portrait__header">
                    <h1 className="arrangement-title">{data.name}</h1>
                    <button onClick={addToCart} 
                        className="app-btn order-btn">Solicitar</button>
                </div>
                <div className="arrangement-portrait__picture">
                    <Slider {...settings}>
                        {
                            data.pictures.file1 && 
                                <div className="arrangement-portrait__picture--slide">
                                    <img className="slide-img" src={data.pictures.file1} />
                                </div>
                        }
                        {
                            data.pictures.file2 && 
                                <div className="arrangement-portrait__picture--slide">
                                    <img className="slide-img" src={data.pictures.file2} />
                                </div>
                        }
                        {
                            data.pictures.file3 && 
                                <div className="arrangement-portrait__picture--slide">
                                    <img className="slide-img" src={data.pictures.file3} />
                                </div>
                        }
                    </Slider>
                </div>
                <div className="arrangement-portrait__desc">
                    <div className="arrangement-autor">
                        <img className="arrangement-autor__img" 
                            src="https://ikebana-app-content.s3-sa-east-1.amazonaws.com/static/mainlogo_transparent_notext.png" alt="perfil" />
                        <p className="arrangement-autor__name">{data.autor} <b>-</b></p>
                    </div>
                    <p className="arrangement-portrait__desc--text">{data.description}</p>
                </div>
                <div className="arrangement-portrait__footer">
                    <div className="arrangement-portrait__footer--likes">
                        <p className="arrangement-likes__num">{data.likes}</p>
                        <button className="arrangement-likes__like">
                        {
                            Object.keys(data.liked_by).includes(userID.toString()) ?
                                <i title="Curtido" className="material-icons">favorite</i>
                                    :
                                <i className=
                                "material-icons">{liked ? 'favorite' 
                                : 'favorite_border'}</i>
                        }
                        </button>
                    </div>
                    <button className="app-btn btn-filled lesson-btn">Aulas</button>
                </div>
            </div>
        </Dialog>
    );
}
