import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import EditLessonDialog from '../../dialog/EditLessonDialog/EditLessonDialog'
import Modal from '../../aux/Modal.js';

import './styles.scss';

export default function LessonDisplay() {
    const [dialog, setDialog] = useState('');

    const options = {
        'edit': EditLessonDialog,
    }

    const ActiveDialog = options[dialog];

    return(
        <>
            <div className={`lesson-element ${dialog !== '' ? 'clicked' : ''}`}>
                <img className="img-cover" src="https://ikebana-app-content.s3-sa-east-1.amazonaws.com/Ikebana_-_Yoshiko_Nakamura_01B.jpg" alt="cover" />
                <div className="lesson-element__content">
                    <button className="lesson-open-btn">
                        <i className="material-icons">remove_red_eye</i>
                        <Link to="/lesson/somehash">Ver</Link>
                    </button>
                    <button 
                        className="lesson-edit-btn">
                        <i className="material-icons">build</i>
                        <Link to="/account/edit_project">
                            Editar
                        </Link>
                    </button>
                    <ul>
                        <li><h4 className="lesson-title">Nome da Aula</h4></li>
                        <div className="description-wrapper">
                            <li><p>28/12/1994</p></li>
                        </div>
                    </ul>
                </div>
            </div>
            {
                dialog &&
                    <Modal>
                        <ActiveDialog handler={() => setDialog('')}/>
                    </Modal>
            }
        </>
    );
}
